import React, { Component } from 'react';
import { Col, Row, Container, Button } from 'reactstrap';
import Header from '../header';
import RandomChar from '../randomChar';
import ErrorMessage from '../errorMessage';
import { CharacterPage, BooksPage, HousesPage, BooksItem, MainPage } from '../pages';
import gotService from '../../services/gotService';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import { Link } from 'react-router-dom';


import './app.css';

export default class App extends Component {
    gotService = new gotService();
    state = {
        isCharVisible: true,
        error: false,
        selectedHouse: 20,
        domens: {
            main: '/main/',
            characters: '/characters/',
            houses: '/houses/',
            books: '/books/'
        }
    }



    componentDidCatch() {
        this.setState({
            error: true
        })
    }

    onCharVisible = () => {
        this.setState((state) => {
            return {
                isCharVisible: !state.isCharVisible
            }
        })
    }

    checkURL(pathname, domens){
        const arr = [];
        for(let key in domens){
            arr.push(domens[key]);
        }
        if(!arr.includes(pathname)){
            return(
                <>
                <h1>ERRROR 404. You are lost. Back to main menu.</h1>
                <Button><Link to="/main">Back to main menu</Link></Button>
            </>
            )
        }
    }
    render() {

        if (this.state.error) {
            return <ErrorMessage></ErrorMessage>;
        }

        const character = this.state.isCharVisible ? <RandomChar></RandomChar> : null;

        const domens = {...this.state.domens};

        return (
            <Router>
                <div className="app">
                    <Container>
                        <Header />
                    </Container>
                    <Container>
                        <Row>
                            <Col lg={{ size: 5, offset: 0 }}>
                                {character}
                                <button
                                    onClick={this.onCharVisible}
                                    className="toggle-btn">Кликни на меня!</button>
                            </Col>
                        </Row>
                        <Route path={domens.main} exact component={MainPage} />
                        <Route path={domens.characters} exact component={CharacterPage} />
                        <Route path={domens.houses} exact component={HousesPage} />
                        <Route path={domens.books} exact component={BooksPage} />
                        <Route path={`${domens.books}:id`} render={
                            ({ match }) => {
                                const { id } = match.params;
                                domens.book = `/books/${id};`
                                return <BooksItem bookId={id} />
                            }} />
                        <Route path='' render={({ location }) => {
                            return this.checkURL(location.pathname, domens);
                        }} />
                    </Container>
                </div>
            </Router>
        );
    };
};
