import React, { Component } from 'react';
import { Col, Row, Container } from 'reactstrap';
import Header from '../header';
import RandomChar from '../randomChar';
import ErrorMessage from '../errorMessage';
import CharacterPage from '../characterPage';
import BooksPage from '../booksPage';
import ItemList from '../itemList';
import ItemDetails from '../itemDetails';
import gotService from '../../services/gotService';

import './app.css';

export default class App extends Component {
    gotService = new gotService();
    state = {
        isCharVisible: true,
        error: false,
    }


    componentDidCatch() {
        this.setState({
            error: true
        })
    }

    onCharVisible = () => {
        this.setState((state) => {
            return {
                isCharVisible: !state.isCharVisible
            }
        })
    }
    render() {

        if (this.state.error) {
            return <ErrorMessage></ErrorMessage>;
        }

        const character = this.state.isCharVisible ? <RandomChar></RandomChar> : null;

        return (
            <>
                <Container>
                    <Header />
                </Container>
                <Container>
                    <Row>
                        <Col lg={{ size: 5, offset: 0 }}>
                            {character}
                            <button
                                onClick={this.onCharVisible}
                                className="toggle-btn">Кликни на меня!</button>
                        </Col>
                    </Row>
                    <CharacterPage></CharacterPage>
                    <BooksPage></BooksPage>
                    <Row>
                        <Col md='6'>
                            <ItemList
                                onItemSelected={this.onItemSelected} 
                                getData={this.gotService.getAllBooks}
                                renderItem={({name, publisher}) => `${name} (${publisher})`}/>
                        </Col>
                        <Col md='6'>
                            <ItemDetails 
                            characterId={this.state.selectedCharacter}
                            bookId={this.state.selectedBook} />
                        </Col>
                    </Row>
                    <Row>
                        <Col md='6'>
                            <ItemList
                                onItemSelected={this.onItemSelected}
                                getData={this.gotService.getAllHouses}
                                renderItem={({name, region}) => `${name} (${region})`} />
                        </Col>
                        <Col md='6'>
                            <ItemDetails 
                            characterId={this.state.selectedCharacter} 
                           />
                        </Col>
                    </Row>
                </Container>
            </>
        );
    };
};
