import React, { Component } from 'react';
import './charDetails.css';
import gotService from '../../services/gotService';
import Spinner from '../spinner';
import ErrorMessage from '../errorMessage';

const Field = ({character, field, label }) => {
    return (
        <li className="list-group-item d-flex justify-content-between">
            <span className="term">{label}</span>
            <span>{character[field]}</span>
        </li>
    )
}

export {
    Field
}

export default class CharDetails extends Component {

    gotService = new gotService();

    state = {
        character: null,
        loading: true,
        error: false
    }

    componentDidMount() {
        this.updateCharacter();
    }

    componentDidUpdate(prevProps) {
        if (this.props.characterId !== prevProps.characterId) {
            this.updateCharacter();
        }
    }

    onCharLoaded = (character) => {
        this.setState({
            character,
            loading: false
        })
    }

    updateCharacter() {
        const { characterId } = this.props;
        if (!characterId) {
            return;
        }

        this.gotService.getCharacter(characterId)
            .then(this.onCharLoaded)
            .catch(() => this.onError())
    }

    onError = () => {
        this.setState({
            error: true,
            character: null
        })
    }

    render() {
        if (!this.state.character && this.state.error) {
            return <ErrorMessage/>
        } else if (!this.state.character) {
            return <span className="select-error"> <Spinner/></span>
        }

        if (this.state.loading) {
            return (
                <div className="char-details rounded">
                    <Spinner/>
                </div>
            )
        }

        const {character}= this.state;

        const {name} = character;



        return (
            <div className="char-details rounded">
                <h4>{name}</h4>
                <ul className="list-group list-group-flush">
                   {
                       React.Children.map(this.props.children, (child) => {
                           return React.cloneElement(child, {character});
                       })
                   }
                </ul>
            </div>
        );
    }
}
