import React, { Component } from 'react';
import { Col, Row, Container, Button} from 'reactstrap';
import Header from '../header';
import RandomChar from '../randomChar';
import ErrorMessage from '../errorMessage';
import {CharacterPage, BooksPage, HousesPage, BooksItem, MainPage} from '../pages';
import gotService from '../../services/gotService';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import {Link} from 'react-router-dom';


import './app.css';

export default class App extends Component {
    gotService = new gotService();
    state = {
        isCharVisible: true,
        error: false,
        selectedHouse: 20
    }
    


    componentDidCatch() {
        this.setState({
            error: true
        })
    }

    onCharVisible = () => {
        this.setState((state) => {
            return {
                isCharVisible: !state.isCharVisible
            }
        })
    }

    checkURL(location, pathes){
    if(pathes.indexOf(location.pathname) === -1){
    return(
        <>
        <Row>
        <Message>ERRROR 404. You are lost. Back to main menu.</Message>
        <Button><Link to="/main">Back to main menu</Link></Button>
        </Row>
        </>
    )
    }
    }

    render() {

        if (this.state.error) {
            return <ErrorMessage></ErrorMessage>;
        }

        const character = this.state.isCharVisible ? <RandomChar></RandomChar> : null;

        const domens = ['/characters/', '/books/', '/houses/', '/main/'];

        return (
            <Router>
            <div className="app">
                <Container>
                    <Header />
                </Container>
                <Container>
                    <Row>
                        <Col lg={{ size: 5, offset: 0 }}>
                            {character}
                            <button
                                onClick={this.onCharVisible}
                                className="toggle-btn">Кликни на меня!</button>
                        </Col>
                    </Row>
                    <Route path={domens[3]} component={MainPage}/>
                    <Route path={domens[0]} component={CharacterPage}/>
                    <Route path={domens[1]} component={HousesPage}/>
                    <Route path={domens[2]} exact component={BooksPage}/>
                     <Route path={`{$domens[2]}:id`} render={
                         ({match}) => {
                        const {id} = match.params;

                        return <BooksItem bookId={id}/>
                         }
                     }/>
                </Container>
                </div>
            </Router>
        );
    };
};
