import React, {Component} from 'react';
import './charDetails.css';
import gotService from '../../services/gotService';
import Spinner from '../spinner';
import ErrorMessage from '../errorMessage';


export default class CharDetails extends Component {

     gotService = new gotService();

     state = {
         character: null,
         loading: true,
         error: false
     }
     
     componentDidMount(){
         this.updateCharacter();
     }
     
     componentDidUpdate(prevProps){
     if(this.props.characterId !== prevProps.characherId){
          this.updateCharacter();
      }
     }

     onCharLoaded = (character) => {
        this.setState({
            character,
            loading: false
        })
    }

     updateCharacter(){

         const {characterId} = this.props;
         if(!characterId){
             return;
         }

         this.setState({
            loading: true
        })

         this.gotService.getCharacter(characterId)
         .then(this.onCharLoaded)
         .catch(() => this.onError())
         }

         onError = ()  => {
            this.setState({
                error: true,
                character: null
            })
            }
    
    render() {
        
        if(!this.state.character && this.state.error){
            return <ErrorMessage></ErrorMessage>
        } else if (!this.state.character){
            return <span className='select-error'>Пожалуйста, выберите персонажа</span>
        }
        
        if(this.state.loading){
            return (
                <div className="char-details rounded">
                <Spinner></Spinner>
               </div>
            )
        }
        const {name, gender, born, died, culture} = this.state.character;
        
        return (
            <div className="char-details rounded">
                <h4>{name}</h4>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item d-flex justify-content-between">
                        <span className="term">Gender</span>
                        <span>{gender}</span>
                    </li>
                    <li className="list-group-item d-flex justify-content-between">
                        <span className="term">Born</span>
                        <span>{born}</span>
                    </li>
                    <li className="list-group-item d-flex justify-content-between">
                        <span className="term">Died</span>
                        <span>{died}</span>
                    </li>
                    <li className="list-group-item d-flex justify-content-between">
                        <span className="term">Culture</span>
                        <span>{culture}</span>
                    </li>
                </ul>
            </div>
        );
    }
}
